import{wordSolver}from"./components/word-solver-0378e7ab42.component.js";let resultDiv=document.getElementById("results"),messagesDiv=document.getElementById("messages"),lettersInput=document.getElementById("input-letters"),MIN_LENGHT=3;function searchWords(){clearResult(),clearMessage();let t=getLetters();wordSolver({letters:t,minLength:MIN_LENGHT,maxLength:t.length}).then(e=>{order(e),setResult(e,t.length)}).catch(e=>{console.error("Falha ao buscar pelas letras!",e),setMessage("<div>Falhou!</div>")})}function clearResult(){resultDiv.innerHTML=""}function setResult(e,t){let n="";for(var s=MIN_LENGHT;s<=t;s++){var l=e.filter(e=>e.length&&e.length==s);l&&l.length&&(n=(n+=`<div class='word-sections'>Com ${s} letras</div>`)+l.map(e=>`<div class="result-item">${e}</div>`).join(""))}resultDiv.innerHTML+=n}function clearMessage(){messagesDiv.innerHTML=""}function setMessage(e){messagesDiv.innerHTML=e}function getLetters(){return lettersInput.value}function order(e){e.sort((e,t)=>e.length!==t.length?e.length-t.length:e.localeCompare(t))}lettersInput.addEventListener("keyup",e=>{e.key&&"ENTER"==e.key.toUpperCase()&&lettersInput.value&&lettersInput.value.length>=MIN_LENGHT&&searchWords()}),document.getElementById("solve-button").addEventListener("click",searchWords),document.getElementById("clear-button").addEventListener("click",function(){clearResult(),lettersInput.value="",lettersInput.focus&&lettersInput.focus()}),document.addEventListener("DOMContentLoaded",function(){lettersInput.focus&&lettersInput.focus()});