import{wordSolver}from"./components/word-solver-0799256287.component.js";let resultDiv=document.getElementById("results"),messagesDiv=document.getElementById("messages"),lettersInput=document.getElementById("input-letters"),MIN_LENGHT=3;function searchWords(){clearResult(),clearMessage();let t=getLetters();wordSolver({letters:t,minLength:MIN_LENGHT,maxLength:t.length}).then(e=>{order(e),setResult(e,t.length)}).catch(e=>{console.error("Falha ao buscar pelas letras!",e),setMessage("<div>Falhou!</div>")})}function clearResult(){resultDiv.innerHTML=""}function setResult(e,t){let s="";for(var n=MIN_LENGHT;n<=t;n++){var l=e.filter(e=>e.length&&e.length==n);l&&l.length&&(s=(s+=`<div class='word-sections'>Com ${n} letras</div>`)+l.map(e=>`<div class="result-item">${e}</div>`).join(""))}resultDiv.innerHTML+=s}function clearMessage(){messagesDiv.innerHTML=""}function setMessage(e){messagesDiv.innerHTML=e}function getLetters(){return lettersInput.value}function order(e){e.sort((e,t)=>e.length!==t.length?e.length-t.length:e.localeCompare(t))}lettersInput.addEventListener("keyup",e=>{"ENTER"==e.key&&lettersInput.value&&lettersInput.value.length>=MIN_LENGHT&&searchWords()}),document.getElementById("solve-button").addEventListener("click",searchWords),document.getElementById("clear-button").addEventListener("click",function(){clearResult(),lettersInput.value="",lettersInput.focus&&lettersInput.focus()});