import{wordSolver}from"./components/word-solver-0799256287.component.js";let resultDiv=document.getElementById("results"),messagesDiv=document.getElementById("messages"),lettersInput=document.getElementById("input-letters");function clearResult(){resultDiv.innerHTML=""}function setResult(e){resultDiv.innerHTML+=e}function clearMessage(){messagesDiv.innerHTML=""}function setMessage(e){messagesDiv.innerHTML=e}function getLetters(){return lettersInput.value}function order(e){e.sort((e,t)=>e.length!==t.length?e.length-t.length:e.localeCompare(t))}document.getElementById("solve-button").addEventListener("click",()=>{clearResult(),clearMessage();var e=getLetters();wordSolver({letters:e,minLength:3,maxLength:e.length}).then(e=>{order(e),setResult(e.map(e=>`<div class="result-item">${e}</div>`).join(""))}).catch(e=>{console.error("Falha ao buscar pelas letras!",e),setMessage("<div>Falhou!</div>")}).finally(()=>{setMessage("<div>Terminou!</div>")})});